import React from "react";
import { Navbar } from "./Navbar";
export function HowToUse(){
return(
    <div style = {{textAlign: "center"}}>
        <Navbar/>
        <div style={{marginLeft : "15rem", marginRight: "15rem"}}>
        <h1>How to Use</h1>
        <p>Instructions for FLIR ONE but valid for any FLIR device able to get a minimum temperature, a maximum temperature, and a thermal image </p>
        <ol>
            <l1>Take thermal image with a FLIR device</l1>
            <li>Upload image to FLIR tools</li>
            <li>Enter the editing tools by tapping on the image and then tapping on the pencil in the top right corner</li>
            <li>Next get the maximum and minimum temperature of the image by looking at the scale on the right side (less accurate) or by using the measurement tool on the bottom left of the screen to draw a box on the entire image to get a minimum and maximum temperature (more accurate)</li>
        </ol>
        </div>
    </div>
)
}
