import {useState} from "react"
export function Form(){

        const [imageFile, setImageFile] = useState(null);
        const [minTemperature, setMinTemperature] = useState('');
        const [maxTemperature, setMaxTemperature] = useState('');
        const [temperatureUnit, setTemperatureUnit] = useState('');
      
        const handleImageChange = (event) => {
          const file = event.target.files[0];
          setImageFile(file);
        };
      
        const handleMinTemperatureChange = (event) => {
          setMinTemperature(event.target.value);
        };
      
        const handleMaxTemperatureChange = (event) => {
          setMaxTemperature(event.target.value);
        };
      
        const handleTemperatureUnitChange = (event) => {
          setTemperatureUnit(event.target.value);
        };
      
        const handleSubmit = async (event) => {
          event.preventDefault();
      
          const formData = new FormData();
          formData.append('imageFile', imageFile);
          formData.append('minTemperature', minTemperature);
          formData.append('maxTemperature', maxTemperature);
          formData.append('temperatureUnit', temperatureUnit);
          console.log(formData)
          try {
            const response = await fetch('http://localhost:5000/upload', {
              method: 'POST',
              body: formData,
            });
      
            // Handle response
          } catch (error) {
            console.log(error)
          }
        };
  
    return(
        <form onChange= {(e) => {handleSubmit(e)}} style = {{textAlign: "center"}} method="post" enctype="multipart/form-data">
        <label for="imageFile" style = {{paddingRight: "20px"}}>Select an image file:</label>
        <br/>
        <input onChange= {(e) => {handleImageChange(e)}} style= {{marginLeft: "5rem"}} type="file" id="imageFile" name="imageFile" accept="image/jpeg, image/jpg, image/png, image/gif" required = {true}/>
        <br/>
        <br/>
        <label>Minimum Temperature: </label>
        <input onChange= {(e) => {handleMinTemperatureChange(e)}} type= "number" style= {{marginLeft: "4.5px", width: "40px"}}/>
        <br/>
        <label>Maximum Temperature: </label>
        <input onChange= {(e) => {handleMaxTemperatureChange(e)}} type= "number" style= {{marginLeft: "4.5px", width: "40px"}}/>
        <br/>
        <br/>
        <label>Units of Minimum and Maximum Temperature</label>
        <br/>
        <input onChange= {(e) => {handleTemperatureUnitChange(e)}} type="radio" id="option1" name="myGroup" value="celsius"/>
        <label for="option1">Celsius</label>
        <br/>
        <input type="radio" id="option2" name="myGroup" value="fahrenheit" style = {{marginLeft: "29px"}}/>
        <label for="option2">Fahrenheit</label>
        <br/>
        <br/>
        <button type="submit">Submit</button>
        </form>
    )

}
